import React, { useState, useEffect } from 'react';

const Tour = (props, deleteTour) => {
  const [tours, setTours] = useState(props);
  const id = props.id;

  function deleteTour() {
    delete tours.data[id];
    console.log(tours);
    setTours(tours);
  }

  return (
    <div className="single-tour">
      <img src={tours.data[id].image} />
      <h3>{tours.data[id].name}</h3>
      <h4 className="tour-price">${tours.data[id].price}</h4>
      <h5 className="tour-info">{tours.data[id].info}</h5>
      <button className="delete-btn"
              onClick={deleteTour()}>Not interested</button>
    </div>
  );
};

export default Tour;
