import React, { useState, useEffect } from 'react';

const Tour = (props) => {
  const id = props.id;
  const tourData = props.data[id];

  useEffect(()=>{
    props.data.splice(id, 1);
    props.data.splice(id, 2);
    props.data.splice(id, 3);
    props.data.splice(id, 4);
  }, []);

console.log(tourData);
  return (
    <div className="single-tour">
      <img src={tourData.image} />
      <h3>{tourData.name}</h3>
      <h4 className="tour-price">${tourData.price}</h4>
      <h5 className="tour-info">{tourData.info}</h5>
      <button className="delete-btn"
              onClick={event => props.data.splice(id, 1)}>Not interested</button>
    </div>
  );
};

export default Tour;
