    <h2>Questions And Answers About Login</h2>
    {data.map(question => (
      <div className="question">
        <h4>{question.title}</h4>
        <button className="btn" onClick={event => displayAnswer()}>+</button>
      </div>
    ))}
  </div>
}

export default App;
