import React from 'react';

const Menu = ({items, category}) => {
  if(category != "All"){
    return <div className="section-center">
      {items.map((item) => (
        <div>
          {(item.category == category) ? <h4>{item.title}</h4>: <></>}
        </div>
      ))}
    </div>
  } else{
    return <div className="section-center">
      {items.map((item) => (
        <article className="menu-item">
          <img src={item.img} className="photo" />
          <div className="item-info">
            <header>
              <h4>{item.title}</h4>
            </header>
            <p className="item-text">{item.desc}</p>
          </div>
        </article>
      ))}
    </div>
  }
};

export default Menu;
