import React from 'react';

const Menu = ({items, category}) => {
  if(category != "All"){
    return <div className="section-center">
      {items.map((item) => (
        <div>
          {(item.category == category) ? <h4>{item.title}</h4>: <></>}
        </div>
      ))}
    </div>
  } else{
    return <div className="section-center">
      {items.map((item) => (
        <div className="menu-item">
          <h4 cl>{item.title}</h4>
          <h4 className="price">{item.price}</h4>
          <p className="item-info">{item.desc}</p>
        </div>
      ))}
    </div>
  }
};

export default Menu;
