import React from 'react';
import MenuCard from './MenuCard';

const Menu = ({items, category}) => {
  if(category != "All"){
    return <div className="section-center">
      {items.map((item) => (
        <div>
          {(item.category == category) ? <h4>{item.title}</h4>: <></>}
        </div>
      ))}
    </div>
  } else{
    return <div className="section-center">
      {items.map((item) => (
        <MenuCard item={item} />
      ))}
    </div>
  }
};

export default Menu;
